INSERT INTO `comments` VALUES (2,'  Áo này thực sự vượt xa mong đợi của tôi! Màu sắc sáng và tươi tắn,\n            vải vóc mềm mịn và rất thoải mái khi mặc. Điểm nhấn thú vị là phong cách \n            thiết kế đơn giản nhưng rất ấn tượng. Tôi rất hài lòng với sản phẩm này.',5,'2023-12-08 14:30:00',1,4),(4,'Chiếc áo này thực sự làm tôi ngạc nhiên! Chất lượng vải tốt, cắt may sắc xảo,\n            và tôi thích cách mà nó phản ánh phong cách của tôi. Tôi sẽ chắc chắn mua thêm \n            một chiếc áo khác từ cửa hàng này. Rất đáng tiền!',5,'2023-12-06 14:30:00',1,3),(12,'Áo này thực sự vượt xa mong đợi của tôi! Màu sắc sáng và tươi tắn, vải vóc mềm mịn và rất thoải mái khi mặc. Điểm nhấn thú vị là phong cách thiết kế đơn giản nhưng rất ấn tượng. Tôi rất hài lòng với sản phẩm này.',2,'2023-12-11 13:23:55',1,1),(108,'Cũng đáng tiền mua lắm!!',5,'2023-12-12 17:45:43',1,5);
